DROP TABLE IF EXISTS `m_videomap`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `m_videomap` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `seq_id` bigint DEFAULT '0',
  `userid` bigint DEFAULT '-1',
  `title` varchar(128) DEFAULT NULL,
  `cover` varchar(128) DEFAULT NULL,
  `path` varchar(128) NOT NULL,
  `link` varchar(128) NOT NULL,
  `description` varchar(512) DEFAULT NULL,
  `date` date DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `userid` (`userid`),
  KEY `cover` (`cover`),
  KEY `UK_LINK` (`link`) USING BTREE,
  CONSTRAINT `m_videomap_ibfk_1` FOREIGN KEY (`userid`) REFERENCES `m_user` (`id`),
  CONSTRAINT `m_videomap_ibfk_2` FOREIGN KEY (`cover`) REFERENCES `m_imgmap` (`url`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8mb3;
/*!40101 SET character_set_client = @saved_cs_client */;

