DROP TABLE IF EXISTS `project`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `project` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `project_name` varchar(500) NOT NULL,
  `project_location` varchar(500) NOT NULL,
  `project_pub_date` date DEFAULT NULL,
  `project_time_range` varchar(500) NOT NULL,
  `project_region` varchar(500) NOT NULL,
  `project_type` varchar(500) NOT NULL,
  `project_target` varchar(500) NOT NULL,
  `project_state` varchar(500) NOT NULL,
  `project_scale` varchar(500) NOT NULL,
  `project_size` varchar(500) NOT NULL,
  `project_cover` varchar(500) NOT NULL,
  `project_description` varchar(5000) NOT NULL,
  `project_address` varchar(500) NOT NULL,
  `project_marker` varchar(500) NOT NULL,
  PRIMARY KEY (`id`),
  CONSTRAINT `project_chk_1` CHECK ((`project_state` in (_utf8mb4'2',_utf8mb4'3',_utf8mb4'4')))
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb3;
